import React from 'react';
import { useState } from 'react';


const useMoneda = () => {
    // state del custom hook
    const [state, setState] = useState ('');

    const Seleccionar = () => (
        <>
            <label>Moneda</label>
            <select>
                <option value="ARG">Pesos Argentinos</option>
            </select>
        </>
    )
    // retornar state, interfaz(q es lo q se va a ver en pantalla) y funcion que modifica es state
    return [ state, Seleccionar, setState ];
}

export default useMoneda;
