import React from 'react';
import { useState } from 'react';
import styled from '@emotion/styled';


const Label = styled.label`
font-family: 'Babel Neue' cursive;
folor: #fff;
text-transform: uppercase;
font-weght: bold;
font-size: 2.4rem;
margin-top: 2rem;
display:block;
`;

const Select = styled.select `
   width: 100%;
   font-size: 1.2rem;
   display:block;
   padding:1rem;
   -webkit-appearance: none;
   border-radius: 10px;
   border: none; `
;


const useMoneda = ({label,stateInicial,opcionesMonedas}) => {
    // state del custom hook
    const [state, setState] = useState (stateInicial);

    const Seleccionar = () => (
        <>
            <Label>{label}</Label>
            <Select
                onChange={e => setState(e.target.value)}
                value={state}>
                <option value="ARG">Selecciona la Moneda</option>
                {opcionesMonedas.map(opcion => (
                    <option key={opcion.codigo}value={opcion.codigo}>{opcion.nombre}</option>
                ))}
            </Select>
        </>
    )
    // retornar state, interfaz(q es lo q se va a ver en pantalla) y funcion que modifica es state
    return [ state, Seleccionar, setState ];
}

export default useMoneda;
