import React from 'react';
import styled from '@emotion/styled';
import useMoneda from '../hooks/useMoneda';

const Boton = styled.input`
  margin-top: 20px;
  font-weight: bold;
  font-size: 20px;
  padding: 10px;
  background-color: #66a2fe;
  border:none;
  width:100%;
  border-radius: 10px;
  color: #fff;
  transition: background-color .3s ease;
    &:hover{
      background-color: #326ac0;
      cursor.pointer;
    }`
;

const Formulario = () => {

  const monedas = [
    {codigo: 'USD', nombre: 'Dolar Estadounidense'},
    {codigo: 'MXN', nombre: 'Peso Mexicxano'},
    {codigo: 'EUR', nombre: 'Euro'},
    {codigo: 'ARG', nombre: 'Peso Argentino'}
 ]

// usamos el hook, podemos llamarlo con otros nombres, lo q importa es el orden
 const [moneda, Seleccionar, setState] = useMoneda('Elige la Moneda','', monedas);

  return (

    <form>  
      <Seleccionar/>
      <Boton
        type='submit'
        value='calcular'>
           COTIZA AHORA
      </Boton>
    </form>
  );
}

export default Formulario;
