import React, { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import styled from "@emotion/styled";
import imagen from './cryptomonedas.png';
import Formulario from "./components/Formulario";
import Cotizacion from "./components/Cotizacion";
import Spinner from "./components/Spinner";

// estilos
const Contenedor = styled.div`
  display: flex;
  max-width: 900px;
  margin: 0 auto;
  @media(max-width:768px) {
      flex-direction: column;
    }`
  ;

const Imagen = styled.img`
  max-width: 80%;
  margin-top:5rem;
`
;
const Heading = styled.h1`
  font-family: 'Bebas Neue', cursive;
  color:#fff;
  text-align:left;
  font-weight:700;
  font-size:50px;
  letter-spacing:3px;
  mergin-bottom:50px;
  margin-top:80px;
    &::after{
      content:'';
      width:100px;
      height:6px;
      background-color:#66A2FE;
      display:block;
    }`
;


function App() {

  const [moneda, setMoneda] = useState ('');
  const [criptomoneda, setCriptomoneda] = useState('');
  const [resultado, setResultado] = useState({});
  const [spinner, setSpinner] = useState (false);

  useEffect (() => {
    const cotizarCriptomoneda = async()=>{
      // evitamos la ejecución la primera vez
      if(moneda === '') return;
      // consultar a la API para obtener la cotización
      const url = `https://min-api.cryptocompare.com/data/pricemultifull?fsyms=${criptomoneda}&tsyms=${moneda}`;
      const resultado = await axios.get(url);
      // mostrar spinner
      setSpinner(true);
      //ocultar el spinner para mostrar el resultado
      setTimeout(()=>{
        // cambiar el estado del spinner
        setSpinner(false);
        // guardar cotización
        setResultado(resultado.data.DISPLAY[criptomoneda][moneda]);
      }, 3000);
    }
    cotizarCriptomoneda();
    // llamamos a  la funcion 
  },[moneda, criptomoneda]);

  // mostrar spinner o resultado
  const componente = (spinner)? <Spinner/> : <Cotizacion resultado={resultado}/>
  return (
    <Contenedor>
      <div>
        <Imagen
          src={imagen}
          alt="Imagen crypto">
        </Imagen>
      </div>
      <div>
        <Heading>Cotiza tu criptomoneda al instante</Heading>
      <Formulario>
        setMoneda = {setMoneda}
        setCriptomoneda = {setCriptomoneda}
      </Formulario>
      {componente}
      </div>
    </Contenedor>
  );
}

export default App;
