import React from "react";
import styled from "@emotion/styled";
import imagen from './cryptomonedas.png';

// estilos
const Contenedor = styled.div`
  max-width: 500px
  margin: 0 auto;
    @media (nim-width:992px){
      display:grid;
      grid-template-columns: repeat(2,1fr);
      column-gap:3rem
    }`
  ;

const Imagen = styled.img`
  max-width: 100%;
  margin-top:5rem;
`
;
const Heading = styled.h1`
  font-family: 'Bebas Neue', cursive;
  color:#fff
  text-align:left;
  font-weight:700;
  font-size:50px;
  mergin-bottom:50px;
  margin-top:80px;
    &::after{
      content:'';
      width:100px;
      height:6px;
      background-color:#66A2FE;
      display:block;
    }`
;


function App() {
  return (
    <Contenedor>
      <div>
        <Imagen
          src={imagen}
          alt="Imagen crypto">
        </Imagen>
      </div>
      <div>
        <Heading>Cotiza tu cripto al instante</Heading>
      </div>
    </Contenedor>
  );
}

export default App;
